package javademo01;

public class Example03Ifelse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int day=1;
		switch (day){
		case 0:
			System.out.println("Sunday");
			break;
		case 1:
			System.out.println("Monday");
			break;
			default:
				System.out.println("wrong option");
		}
	}

}
