package arraysdemo;

public class Arrdemo03 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String[] days=new String[7];
		days[0]="sunday";
		days[1]="Monday";
		days[2]="tuesday";
		days[3]="wednesday";
		days[4]="thursday";
		days[5]="friday";
		days[6]="saturday";
		for(int i=0;i<days.length;i++){
			System.out.print(days[i]+"\t");
		}
	}

}
